package bg.o.sim.colourizmus.view;import android.graphics.Color;import android.os.Bundle;import android.os.CountDownTimer;import android.support.v7.app.AppCompatActivity;import java.util.Random;import bg.o.sim.colourizmus.R;public class EpilepticaActivity extends AppCompatActivity {@Override protected void onCreate(Bundle savedInstanceState){super.onCreate(savedInstanceState);setContentView(R.layout.activity_epileptica);(findViewById(R.id.epileptica_button)).setOnClickListener(v -> {v.setClickable(false);final Random r = new Random();CountDownTimer cd = new CountDownTimer(7000, 66){@Override public void onTick(long millisUntilFinished){findViewById(R.id.activity_epileptica_base).setBackgroundColor(Color.rgb(r.nextInt(256), r.nextInt(256), r.nextInt(256)));}@Override public void onFinish(){v.setClickable(true);}};cd.start();});}}
